class Solution {
public:
    map<pair<int,pair<int,int>>,int>dp;
    int fun(long long indx,int k,int cur,int jump)
    {
        if(indx-k>1)
            return 0;
        if(dp.find({jump,{indx,cur}})!=dp.end())
            return dp[{jump,{indx,cur}}];
        int ans=0;
        if(indx==k)
            ans++;
        if(!cur)
        {
            if(indx)
            ans+=fun(indx-1,k,1,jump);    
        }
        ans+=fun(indx+powl(2,jump),k,0,jump+1);
        return dp[{jump,{indx,cur}}]=ans;
    }
    int waysToReachStair(int k) {
        return fun(1,k,0,0);
    }
};
