class Solution {
public:
    int waysToReachStair(int k) {

        int ans = 0;
        long long choose[35][35] = {};
        for (int i=0; i<35; i++) for (int j=0; j<=i; j++){
            if (j == 0 || j == i) choose[i][j] = 1;
            else choose[i][j]= choose[i-1][j-1] + choose[i-1][j];
        }
        for (long long jump=0; jump<33; jump++){
            long long destination = (long long)1<<(jump);
            if (destination < k) continue;
            long long toomuch = destination - k;
            if (toomuch <= jump+1){
                ans += choose[jump+1][toomuch];
            }
        }
        return ans;
    }
};
