class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache
        def go(k,jump,isLast):
            if k < 0:
                return 0
            ans = 0
            if k == 1:
                ans += 1
            if not isLast:
                ans += go(k+1,jump,True)
            ans += go(k - (2**jump),jump+1,False)
            return ans
        return go(k,0,False)
