class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, jump, prev_down):
            if i == k:
                res = 1  
            else:
                res = 0

            if i >= 1 and not prev_down: 
                res += dp(i - 1, jump, True)

            nexts = i + 2**jump
            if nexts <= k + 1:
                res += dp(nexts, jump + 1, False)

            return res

        return dp(1, 0, False)
