class Solution:
    def waysToReachStair(self, k: int) -> int:

        dp = {}

        def solve(i,j,f):
            if i<0 or i-k>4: 
                return 0 

            if i == k:
                res = 1 + solve(i+(2**j),j+1,1)
                if f: 
                    res+=solve(i-1,j,0)

                dp[(i,j)] = res
                return res


            if (i,j,f) in dp:
                return dp[(i,j,f)]

            ans = 0 
            if f:
                ans = solve(i-1,j,0)

            ans += solve(i+(2**j),j+1,1)

            dp[(i,j,f)] = ans
            return ans 

        return solve(1,0,1)
