class Solution {
public:
    map<pair<int,pair<int,bool>>,int> mp;
    int waysToReachStair(int k) {
        int ans=rec(1,1,false,k);
        return ans;
    }
    int rec(long long i, long long jump, bool last, int &k)
    {
        if(i>k+1)
            return 0;
        if(mp.find({i,{jump,last}})!=mp.end())
            return mp[{i,{jump,last}}];
        int ans=0;
        if(i==k)
            ans++;
        if(i!=0 && !last)
        ans+=rec(i-1,jump,true,k);
        i+=jump;
        jump*=2;
        ans+=rec(i,jump,false,k);
        jump/=2;
        i-=jump;
        return mp[{i,{jump,last}}]=ans;
    }
};
