from math import comb

class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        ans = 0
        for i in range(32):
            target = (2**i) - k
            if target >= 0 and target <= (i+1):
                ans += comb(i+1, target)
        return ans
