class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def dfs(i,j,down):
            res = 0
            if i == k:
                res+=1
            if i + 2**j <=k+1:
                res+=dfs(i+2**j,j+1,False)
            if down == False:
                res+=dfs(i-1,j,True)
            return res
        return dfs(1,0,False)
