class Solution {
public:
    int waysToReachStair(int n) {
        int i, j, T, res = 0, f[31][31], x;
        if (n == 0 || n == 1) ++res;
        for (i = 0; i <= 30; ++i){
            f[i][0] = f[i][i] = 1;
            for (j = 1; j < i; ++j)
                f[i][j] = (f[i - 1][j - 1] + f[i - 1][j]);
        }

        for (T = 0; T <= 29; ++T){
            x = (1<<(T + 1));
            // cout<<x<<" "<<T + 2<<" "<<x - n<<endl;
            if (x >= n && x - n <= T + 2) res += f[T + 2][x - n];
        }

        return res;
    }
};
