class Solution:
    def waysToReachStair(self, k: int) -> int:
        pwrs = [2 ** i for i in range(31)]

        @cache
        def go(i=1, jmp=0, fst=False):
            ret = i == k
            if i + pwrs[jmp] - 1 > k and i - 1 > k:
                return ret
            ret += go(i + pwrs[jmp], jmp + 1, False)
            if not fst:
                ret += go(i - 1, jmp, True)
            return ret

        return go()
