class Solution {
public:
    unordered_map<int,unordered_map<int,unordered_map<int,int>>> dp;
    int helper(int k,bool done,int pow1){
        if (k<0) return 0;
        if (k==0 ) return (done==0);
        if (dp[k][done].count(pow1)) return dp[k][done][pow1];
        int ans=(k==1)+helper(k-pow(2,pow1),0,pow1+1);
        if (done==0) ans+=helper(k+1,1,pow1);
        return dp[k][done][pow1]=ans;

    }
    int waysToReachStair(int k) {
        if (k==0) return 2;
        return helper(k,0,0);
    }
};
