class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k<5:
            return [2,4,4,3,2][k]
        a=4
        b=8
        while b<k:
            b*=2
            a+=1
        if b-a<=k<=b:
            x=a
            y=b-k
            return comb(x,y)
        return 0
