class Solution:
    def waysToReachStair(self, k: int) -> int:
        def choose(i, j):
            ans = 1
            for k in range(j):
                ans *= (i-k)
            for k in range(1, j+1):
                ans //= k
            return ans
        n = 1
        i = 1
        ans = 0
        while i-n <= k:
            if i >= k:
                ans += choose(n, i-k)
            n += 1
            i *= 2
        return ans
