class Solution:

    def waysToReachStair(self, k: int) -> int:
      def getSum(jump, down):
        return 2**(jump) - down
      dp = defaultdict(int)
      dp[(0, 0, False)] = 1

      for jump in range(40):
        for down in range(40):
          dp[(jump+1, down, False)] = dp[(jump, down, True)] + dp[(jump, down, False)]
          if getSum(jump, down) != 0:
            dp[(jump, down+1, True)] = dp[(jump, down, False)]
      ans = 0
      for jump, down, downBefore in dp:
        if getSum(jump, down) == k:
          #print(jump, down)
          ans += dp[(jump, down, downBefore)]
      return ans
