class Solution:
    def waysToReachStair(self, k: int) -> int:
        N = 40
        C = [[0 for j in range(N)] for i in range(N)]
        for i in range(N):
            C[i][0] = 1
            C[i][i] = 1
            for j in range(1, i):
                C[i][j] = C[i - 1][j] + C[i - 1][j - 1]

        ans = 0
        for i in range(0, N):
            u = 1 << i
            v = u - (i + 1)
            if v <= k <= u:
                ans += C[i + 1][u - k]

        return ans
