class Solution {
public:
    unordered_map<int,int> dp[34][2];
    int func(int k, long long i=1, int jump=0, int prev=0)
    {
        if(i>k+1)
            return 0;
        auto it=dp[jump][prev].find(i);
        if(it!=dp[jump][prev].end())
            return it->second;

        int ans=0;
        if(i==k)
            ans++;
        int opt1=func(k,i+(1<<jump),jump+1,0);
        int opt2=0;
        if(prev!=1)
            opt2=func(k,i-1,jump,1);
        ans+=opt1+opt2;
        dp[jump][prev][i]=ans;
        return ans;
    }
    int waysToReachStair(int k) {
        return func(k);
    }
};
