class Solution:
    def waysToReachStair(self, k: int) -> int:
        if k == 0:
            return 2
        if k == 1:
            return 4


        # n 里面选 m
        def solve(n, m):
            tmp = 1
            for i in range(n, n - m, - 1):
                tmp *= i
            for i in range(1, m + 1):
                tmp //= i
            return tmp


        cur = 1
        res = 0
        for i in range(32):
            cur += 1 << i
            right = cur
            left = right - i - 2
            if left <= k <= right:
                a = i + 2
                b = right - k
                # print(left, right)
                # print(a, b)
                res += solve(a, b)





        return res
