class Solution {
public:
    int func(int jp, int bk, int pv, vector<vector<vector<int>>> &dp, int k) {

        int pos = 1-bk;
        for(int i = 0; i < jp; i++) {
            pos += (1<<i);
        }
        if((pos-k)>1) return 0;
        if(dp[jp][bk][pv]!=-1) return dp[jp][bk][pv];
        int ans = (pos==k);
        ans += func(jp+1,bk,0,dp,k);
        if(!pv) ans += func(jp,bk+1,1,dp,k);
        return dp[jp][bk][pv] = ans;
    }

    int waysToReachStair(int k) {
        vector<vector<vector<int>>> dp(60,vector<vector<int>>(60,vector<int>(2,-1)));
        return func(0,0,0,dp,k);
    }
};
