class Solution {
public:

    unordered_map<long long, int> dp;

    int solve(int s, int jump, bool d, int k) {
        if(s > k + 1) return 0;
        long long h = (1ll * s << 8ll) + (jump << 1) + d;
        if(dp.find(h) != dp.end()) return dp[h];
        int ans = s == k;
        if(s != 0 && d != 1) ans += solve(s - 1, jump, 1, k);
        ans += solve(s + (1 << jump), jump + 1, 0, k);
        return dp[h] = ans;
    }

    int waysToReachStair(int k) {
        return solve(1, 0, 0, k);
    }
};
