class Solution:
    def waysToReachStair(self, k: int) -> int:
        def recurse(i, jump, canjump, memo):
            if (i, jump, canjump) in memo:
                return memo[(i, jump, canjump)]
            elif i > k + 1:
                return 0
            ans = 1 if i == k else 0
            if canjump:
                ans += recurse(i-1, jump, False, memo)
            ans += recurse(i + 2**jump, jump + 1, True, memo)
            memo[(i, jump, canjump)] = ans
            return ans
        return recurse(1, 0, True, {})
