class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dp(i, jump, back): # back = 0/1
            # print(i, jump, back)

            res = 1 if i == k else 0

            j = i + (1 << jump)
            if j <= k + 1: # do jump
                res += dp(j, jump + 1, 1)

            if i > 0 and back == 1:
                res += dp(i - 1, jump, 0)
            return res

        return dp(1, 0, 1)
