class Solution {
public:
    #define ll long long
    // dp[l][t][p]
    ll rec(ll l,ll t,ll p,map< pair<pair<ll,ll>,ll >,ll>& dp,int& k){
        if(l>k+1)return 0;

        if(dp[{{l,t},p}]!=0)return dp[{{l,t},p}];
        ll ans=(l==k);
        if(l>0 && t==0){
            ll ans1=rec(l-1,1,p,dp,k);
            if(ans1!=-1)ans+=ans1;
        }
        ll ans2=rec(l+(1ll<<p),0,p+1,dp,k);
        if(ans2!=-1)ans+=ans2;
        // cout<<l<<" "<<t<<" "<<p<<" "<<ans<<endl;
        if(ans==0)ans=-1;
        return dp[{{l,t},p}]=ans;

    }
    int waysToReachStair(int k) {
        map<pair<pair<ll,ll>,ll>,ll> aa;
        ll ans=rec(1,0,0,aa,k);
        if(ans==-1)ans=0;
        return ans;
    }
};
