using namespace std;  
using ll = long long;
#define FOR(i, a, b) for (int i = a; i < b; ++i)
#define ve vector
const int N  = 55;
ll c[N][N];
void init() {
    c[0][0] = 1;
    for(int i = 1;i <= 50; i++) {
      c[i][0]=1;
      for(int j = 1;j <= i;j++) {
        c[i][j] = ( c[i-1][j] + c[i-1][j-1] );
      }
    }
}

class Solution {
public:
    int waysToReachStair(int k) {
        init();
        ll ans = 0;
        if(k == 1 || k == 0) ans++;
        ll s = 1;
        for(int i = 0; i <= 40; i++) { //number of jumps
            s += (1ll << i);
            if(s < k) continue;
            ll need = s - k;
            int count = (i + 1 + 1);
            if(need > count) continue;
            ans += c[count][need];
        }

        return ans;
    }
};
