class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans = 0
        # 枚举倒退步数
        for dec in range(60):
            # 前进步数等于 倒退步数 + k - 1
            inc = dec + k - 1
            if inc == 0:
                ans += dec < 2
            elif inc > 0:
                s = bin(inc)[2:]
                if s.count('0') == 0:
                    if len(s) + 1 >= dec:
                        ans += comb(len(s) + 1, dec)
        return ans
