class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def dp(num, jump, has_down):
            if num > max(k * 2, 8):
                return 0
            ret = dp(num + (1 << jump), jump + 1, False)
            if (not has_down) and num > 0:
                ret += dp(num - 1, jump, True)
            # print((num, jump, has_down), ret)
            if num == k:
                return ret + 1
            return ret

        return dp(1, 0, False)
