class Solution {
public:

    int getAns(int k, long long curr, long long jump,int used,map<long long, long long > &mp)
    {
        if(jump >0 && curr-k >1) return 0;

        long long mask =0;
        mask = mask | (curr <<33);
        mask = mask | (jump <<1);
        mask = mask | (used);
        if(mp.find(mask) != mp.end()) return mp[mask];
        int x= 0;
        if(curr >0 && used==0)
        {
           x= getAns(k,curr-1,jump,1,mp);
        }
        int y = getAns(k,curr+pow(2,jump),jump+1,0,mp);
        return mp[mask]=(x+y + (curr==k? 1:0));
    }
    int waysToReachStair(int k) {
        map<long long, long long > mp;
        return getAns(k,1,0,0, mp);
    }
};
