class Solution:
    def waysToReachStair(self, k: int) -> int:


        @cache
        def dfs(curStep, jump, useBack):
            if curStep > k + 10: return 0

            res = 0
            if curStep == k:
                res += 1

            if useBack == 0:
                res += dfs(curStep - 1, jump, 1)
            res += dfs(curStep + 2 ** jump, jump + 1, 0)
            return res


        return dfs(1, 0, 0)
