class Solution:
    def waysToReachStair(self, k: int) -> int:
        ans=[0]
        dp={}
        def qwe(i,j,flag):
            if i>k+1 or i<0:
                return 0
            if (i,j,flag) in dp:
                return dp[(i,j,flag)]

            ans=0
            if i==k:
                ans=1
            if flag:
                dp[(i,j,flag)]= ans+qwe(i+2**j,j+1,False)
                return dp[(i,j,flag)]
            # print(ans+(i+2**j,j+1,False),(i-1,j,True))
            dp[(i,j,flag)]= ans+qwe(i+2**j,j+1,False)+qwe(i-1,j,True)
            return dp[(i,j,flag)]

        # return -1
        return qwe(1,0,False)
