class Solution {
public:
    int waysToReachStair(int k) {
        map<tuple<int,int,int>,int> cache;
        auto calc=[&](auto calc, int cur, int jump, int back)->int{
            if (cur>k+1){
                return 0;
            }

            tuple<int,int,int> tp=make_tuple(cur,jump,back);
            if(cache.count(tp)){
                return cache[tp];
            }

            int res=(cur==k?1:0)+calc(calc, cur+(1<<jump),jump+1,1);
            if (back>0&&cur>0){
                res+=calc(calc,cur-1,jump,0);
            }
            return cache[tp]=res;
        }; 

        return calc(calc,1,0,1);
    }
};
