class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(i, canI, jump):
            if i < 0 or i > k + 1:
                return 0
            ans = 0
            if i == k:
                ans += 1

            if canI:
                ans += dp(i - 1, False, jump)

            ans += dp(i + 2**jump, True, jump + 1)
            return ans

        return dp(1,True, 0)
