class Solution:
    def waysToReachStair(self, k: int) -> int:

        @cache
        def backtrack(cur, jumps, prev_back):
            res = 0
            if cur == k:
                res += 1
            if cur + (2 ** jumps) <= k + 1:
                res += backtrack(cur + (2 ** jumps), jumps + 1, False)
            if not prev_back:
                res += backtrack(cur - 1, jumps, True)
            return res



        return backtrack(1, 0, False)
