class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dp(prev, i, jump):
            ans = 0
            if i > k+1: return ans
            if i == k: ans += 1
            ans += dp(0, i+(1<<jump), jump+1)
            if not prev: ans += dp(1, i-1, jump)
            return ans
        return dp(0, 1, 0)
