class Solution:
  def waysToReachStair(self, k: int) -> int:
    def dfs(i, jump, flag):
      if i > k + 1:
        return 0
      key = (i, jump, flag)
      if key in cache:
        return cache[key]
      res = dfs(i + (1 << jump), jump + 1, True)
      if i == k:
        res += 1
      if flag and i > 0:
        res += dfs(i - 1, jump, False)
      cache[key] = res
      return res

    cache = dict()
    return dfs(1, 0, True)
