                    q.push({a, b, c});
                }
                hm[a][b][c] += hm[v[0]][v[1]][v[2]];

            } else {
                // go 1
                {
                    LL a = v[0]-1;
                    LL b = 1;
                    LL c = v[2];
                    if (hm.count(a) && hm[a].count(b) && hm[a][b].count(c)) {
                        ;
                    } else {
                        q.push({a, b, c});
                    }
                    hm[a][b][c] += hm[v[0]][v[1]][v[2]];
                }
                // go 2
                {
                    LL a = v[0] + (1<<v[2]);
                    LL b = 2;
                    LL c = v[2] + 1;
                    if (hm.count(a) && hm[a].count(b) && hm[a][b].count(c)) {
                        ;
                    } else {
                        q.push({a, b, c});
                    }
                    hm[a][b][c] += hm[v[0]][v[1]][v[2]];
                }
            }
        }

        for (auto & [_, hm1] : hm[k]) {
            for (auto & [_, v] : hm1) {
                res += v;
            }
        }
        return res;
    }
};
