typedef pair<int, int> ii;
typedef pair<int, ii> iii;

class Solution {
private:
    map<iii, int> f;    
public:
    int dp(int cur, int jump, bool pre, int k) {
        if (cur >= k + 2)
            return 0;
        if (f.count(iii(cur, ii(jump ,pre))))
            return f[iii(cur, ii(jump, pre))];
        int ans = (cur == k);
        if (!pre) ans += dp(cur - 1, jump, true, k);
        ans += dp(cur + (1 << jump), jump + 1, false, k);

        return f[iii(cur, ii(jump, pre))] = ans;
    }
    int waysToReachStair(int k) {
        return dp(1, 0, 0, k);
    }
};
