class Solution:
    def waysToReachStair(self, k: int) -> int:
        def c(n, k):
            ans=1
            for i in range(1, k+1):
                ans=ans*(n+1-i)//i
            return ans
        v=1
        j=0
        ans=0
        while v-j-1<=k:
            if k<=v:
                jumps=v-k
                ans+=c(j+1, jumps)
            v+=(1<<j)
            j+=1
        return ans
