class Solution {
public:
    int waysToReachStair(int k) {
        map<pair<long long, long long>, int> mp;
        mp[{1, 0}]=1;

        int ret = 0;
        while(!mp.empty()){
            auto [state, c] = *mp.begin();
            auto [x, j] = state;
            mp.erase(mp.begin());
            // cout<<"x="<<x<<",j="<<j<<",c="<<c<<endl;

            if(x==k) ret+=c;
            if(x-1==k) ret+=c;
            long long d = 1<<j;
            if(x+d<=k+1){
                mp[{x+d, j+1}] += c;
            }
            if(x>0&&x-1+d<=k+1){
                mp[{x-1+d, j+1}] += c;
            }
        }
        return ret;
    }
};
