import math

class Solution:
    def waysToReachStair(self, k: int) -> int:
        sol_small = [2, 4, 4, 3, 2, 4, 6, 4, 1]
        if k < 9:
            return sol_small[k]

        t = 1
        y = 0
        while t < k:
            t = t * 2
            y += 1
        diff = t - k

        return math.comb(y + 1, diff)
