class Solution:
    def waysToReachStair(self, k: int) -> int:
        #state -> (pos, jump, wentBack)
        @lru_cache
        def f(pos, jump, wentBack):
            if(pos > k + 1):
                return 0
            ans = 0
            if(pos == k):
                ans = 1
            if(not wentBack):
                if(pos > 0):
                    ans += f(pos - 1, jump, True)
            ans += f(pos + 2 ** jump, jump + 1, False)
            return ans
        return f(1, 0, False)
