class Solution {
public:
    long long nCr(int n, int r)
{

    long long p = 1, k = 1;

    if (n - r < r)
        r = n - r;

    if (r != 0) {
        while (r) {
            p *= n;
            k *= r;

            long long m = __gcd(p, k);
            p /= m;
            k /= m;

            n--;
            r--;
        }
    }

    else
        p = 1;

    return p;
}

    int waysToReachStair(int k) {
        if(k==0) return 2;
        if(k==1) return 4;
        if(k==2) return 4;
        if(k==3) return 3;
        if(k==4) return 2;
        long long num  = 1, i=1;
        while(num<k){
            num=(1<<i);
            i++;
        }
        long long diff = num-k;
        if(diff>i) return 0;
        return nCr(i,diff);
    }
};
