#define ll long long
class Solution {
public:
    ll C(ll n, ll k) {
        ll res = 1LL;
        for (ll i = 1; i <= k; ++i) res = res * (n - k + i) / i; 
        return (ll)(res+0.01);
    }
    int waysToReachStair(int k) {
        if (k == 0 || k == 1) return 1 << (k + 1);
        ll ans = 0;
        for (int j = 0; j < 31; ++j) {
            int rr = 1 << j;
            if (rr < k) continue;
            int q = rr - k;
            if (q > j + 1) {
                continue;
            } else {
                ans += C(j + 1, q);
            }
        }
        cout << ans << endl;
        return (int) ans;
    }
};
