class Solution:
    def waysToReachStair(self, k: int) -> int:
        @cache
        def dfs(i = 1, jump = 0, go_back = False):
            if i > k + 1:
                return 0
            #if i > k and go_back:
                #return 0
            #    return 1 if i - 1 == k and not go_back else 0
            if go_back:
                return int(i==k) + dfs(i + 2**jump, jump+1, False)
            return int(i == k) + dfs(i + 2**jump, jump+1, False) + dfs(i - 1, jump, True)
        return dfs()
