class Solution:
    def waysToReachStair(self, k: int) -> int:
        @lru_cache(None)
        def helper(index, jump, down):
#             print(index, jump, down)
            if index - 1 > k or index < 0:
                return 0

            res = 0
            if index == k:
                res += 1
            if down == False:
                res += helper(index - 1, jump, True)
            res += helper(index + pow(2, jump), jump + 1, False)
            return res
        res = helper(1, 0, False)
        return res
