class Solution:
    def waysToReachStair(self, k: int) -> int:


        @lru_cache(None)
        def dfs(i, j, last_dec):

            if last_dec and i > k:
                return 0

            if i > k + 2:
                return 0

            cnt = 0

            if i == k:
                cnt += 1
            if i > 0 and not last_dec:
                cnt += dfs(i - 1, j, True)

            cnt += dfs(i + 2 ** j, j + 1, False)
            return cnt



        return dfs(1, 0, False)
