class Solution:
    def waysToReachStair(self, k: int) -> int:

        mem = {}
        def helper(i, j, canBack):
            if i >= k + 2:
                return 0
            if (i, j, canBack) in mem:
                return mem[(i, j, canBack)]
            temp = 0
            if i == k:
                temp += 1

            if canBack:
                temp += helper(i - 1, j, False)
            temp += helper(i + 2 ** j, j + 1, True)

            mem[(i, j, canBack)] = temp
            return temp

        return helper(1, 0, True)
