class Solution:
    def waysToReachStair(self, k: int) -> int:

        jump = 0
        pos = 1

        @cache
        def helper(pos, jump, k): 
            #print(pos, jump, k)
            if pos > k + 1: return 0
            res = 0
            if pos == k: res += 1
            if pos == k + 1: res += 1
            if pos > 0: 
                new = pos - 1 + 2 ** jump
                res += helper(new, jump + 1, k)
            res += helper(pos + 2 **jump, jump + 1, k)
            return res

        return helper(1, 0, k)
