class Solution:
    def waysToReachStair(self, k: int) -> int:



        @cache
        def check(i,j,c):
            v = 0
            if i == k:
                v = 1
            if i < 0 or i > k + 1:
                return 0
            else:
                if c and i != 0:
                    v += check(i-1, j, 0)
                    v += check(i + 2 ** j, j + 1, 1)
                else:
                    v += check(i + 2 ** j, j + 1, 1)
                return v
        return check(1,0,1)
