class Solution:
    def waysToReachStair(self, k: int) -> int:
        def perm(n,c):
            x = 1
            for i in range(1,n-c+1):
                x *= (c+i)
                x //= i
            return x
        total = 0
        jump = 0
        s = 1
        for i in range(34):
            if(s >= k and s-k <= i+1):
                total += perm(i+1,s-k)

            s += 2**jump
            jump += 1
        return total
