#define ll long long
class Solution {
    map<array<ll,3>,ll> dp;
    ll f(ll c,ll j,ll t,ll k) {
        if(c>k+1) return 0;
        if(c==k+1) return t;
        if(c==k) return 1;
        if(dp.find({c,j,t})!=dp.end()) return dp[{c,j,t}];
        ll x=0;
        if(t==1 && c>0) x+=f(c-1,j,0,k);
        x+=f(c+(1ll<<j),j+1,1,k);
        return dp[{c,j,t}]=x;
    }
public:
    int waysToReachStair(int k) {
        if(k==0) return 2;
        if(k==1) return 4;
        if(k==2) return 4;
        dp.clear();
        return f(1,0,1,k);
    }
};

