#include <bits/stdc++.h>
using namespace std;

#define FOR(i, x, y) for (int i = x; i < y; i++)
#define ll long long 
#define pii pair<int, int>
#define pll pair<ll, ll>

ll choose[35][35];
bool done = false;

void genChoose(int n){
    if (done)
        return;

    for (int i = 0; i < n and (choose[i][0] = 1); i++)
        for (int j = 1; j <= i; j++)
            choose[i][j] = (choose[i - 1][j] + choose[i - 1][j - 1]);

    done = true;
}

class Solution {
public:
    int waysToReachStair(int k) {
        genChoose(34);

        ll ans = 0;

        for (int jmp = 0; jmp <= 30; jmp++){
            ll land = (1 << (jmp));
            ll back = land - k;

            if (back < 0)
                continue;

            ll spots = jmp + 1;

            if (back <= spots){
                ans += choose[spots][back];
            }
        }
        return ans;
    }
};
